/*!

=========================================================
* Paper Dashboard React - v1.3.0
=========================================================

* Product Page: https://www.creative-tim.com/product/paper-dashboard-react
* Copyright 2021 Creative Tim (https://www.creative-tim.com)

* Licensed under MIT (https://github.com/creativetimofficial/paper-dashboard-react/blob/main/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, {useEffect, useState} from "react";

// reactstrap components

import axios from "axios";

import { Button, Checkbox, Form } from 'semantic-ui-react';
import {Modal} from "react-bootstrap";

function Salle() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [name, setName] = useState('');
    const [type, settype] = useState('');
    const [b, setb] = useState('');
    const postData = () => {
        axios.post(`http://localhost:3000/salles`, {
            name:"ééé",
            type:"ééééé",
            bloc:b

        })
    }
    const [APIData, setAPIData] = useState([]);
    useEffect(() => {

    }, [])
    useEffect(() => {
        axios.get(`http://localhost:3000/salles`)
            .then((response) => {
                setAPIData(response.data);
            })
    }, [])


    useEffect(() => {
        axios.get(`http://localhost:3000/blocs/61c1227c361e0304847f3c99`)
            .then((response) => {
                setb(response.data);
                console.log(APIData);
            })
    }, [])

    const onDelete = (id) => {
        axios.delete(`http://localhost:3000/blocs/${id}`).then(console.log(APIData));

    }

    function test() {
        console.log(APIData);

    }

    return (
        <>
            <div class="container ">
                <div className="crud shadow-lg p-3 mb-5 mt-5 bg-body rounded">
                    <div class="row ">

                        <div class="col-sm-3 mt-5 mb-4 text-gred">
                            <div className="search">
                                <form class="form-inline">
                                    <input class="form-control mr-sm-2" type="search" placeholder="Search Student" aria-label="Search"/>

                                </form>
                            </div>
                        </div>
                        <div class="col-sm-3 offset-sm-2 mt-5 mb-4 text-gred" style={{color:"green"}}><h2><b>Student Details</b></h2></div>
                        <div class="col-sm-3 offset-sm-1  mt-5 mb-4 text-gred">
                            <Button variant="primary" onClick={handleShow}>
                                Add New Student
                            </Button>
                        </div>
                    </div>
                    <div class="row">
                        <div class="table-responsive " >
                            <table class="table table-striped table-hover table-bordered">
                                <thead>
                                <tr>
                                    <th>#</th>
                                    <th>University </th>
                                    <th>name</th>
                                    <th>Actions</th>
                                </tr>
                                </thead>
                                <tbody>

                                </tbody>
                            </table>
                        </div>
                    </div>

                    {/* <!--- Model Box ---> */}
                    <div className="model_box">
                        <Modal
                            show={show}
                            onHide={handleClose}
                            backdrop="static"
                            keyboard={false}
                        >
                            <Modal.Header closeButton>
                                <Modal.Title>Add Record</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                <form>
                                    <div class="form-group">
                                        <input
                                            defaultValue="ENSAJ"
                                            disabled
                                            placeholder="Company"
                                            type="text"
                                        />                                    </div>
                                    <div class="form-group mt-3">
                                        <input
                                            placeholder=' Name' onChange={(e) => setName(e.target.value)}
                                            id="name"
                                            name="name"
                                            placeholder="name"
                                            type="text"
                                        />
                                        <input
                                            placeholder=' type' onChange={(e) => settype(e.target.value)}
                                            id="type"
                                            name="type"
                                            placeholder="name"
                                            type="text"
                                        />
                                        <input
                                            placeholder=' Name' onChange={(e) => setName(e.target.value)}
                                            id="bloc"
                                            name="bloc"
                                            placeholder="name"
                                            type="text"
                                        />
                                    </div>
                                    <button type="submit" class="btn btn-success mt-4" onClick={ postData}>Add Record</button>
                                </form>
                            </Modal.Body>

                            <Modal.Footer>
                                <Button variant="secondary" onClick={handleClose}>
                                    Close
                                </Button>

                            </Modal.Footer>
                        </Modal>

                        {/* Model Box Finsihs */}
                    </div>
                </div>
            </div>
        </>

    );
}

export default Salle;
